// server.js
const express = require("express");
const bodyParser = require("body-parser");
const fs = require("fs");
const path = require("path");
const cors = require("cors");

const app = express();
const PORT = 3000;

// Middleware
app.use(cors());
app.use(bodyParser.json());
app.use(express.static("public")); // Make sure your HTML/CSS/JS is in a "public" folder

// ✅ Handle booking POST requests
app.post("/book", (req, res) => {
  const booking = req.body;
  console.log("Booking received:", booking);

  const filePath = path.join(__dirname, "bookings.json");

  let bookings = [];

  // ✅ If file exists, read it
  if (fs.existsSync(filePath)) {
    const data = fs.readFileSync(filePath, "utf8");
    try {
      bookings = JSON.parse(data);
    } catch (err) {
      bookings = []; // fallback if file is corrupted
    }
  }

  // ✅ Add new booking to array
  bookings.push(booking);

  // ✅ Save updated array to file
  fs.writeFileSync(filePath, JSON.stringify(bookings, null, 2));

  res.status(200).send("✅ Booking successful!");
});

// ✅ Start server
app.listen(PORT, () => {
  console.log(`✅ Server running at http://localhost:${PORT}`);
});
